    message: PropsType
}
type PropsType = {
    id: number

    user: PropsT 
    message: PropsTT
}
type PropsT = {
    avatar: string
    name: string
}
type PropsTT = {
    text: string
    time: string
}

// нужно отобразить приходящие данные
const Message = (props: MessagePropsType) => {
    return ( 

        <div id={'hw1-message-' + props.message.id} className={s.message}>
            <div className={s.imageAndText}>
                <img
                    id={'hw1-avatar-' + props.message.id}
                    // создаёт студент
