const initState: StateType = {
    themeId: 1,
}

export const themeReducer = (state = initState, action: SetThemeActionType): StateType => { // fix any
    switch (action.type) {
        // дописать
        case 'SET_THEME_ID': 
            return {...state, themeId: action.value}
        default:
            return state
    }
}

export const changeThemeId = (id: number): StateType => ({ type: 'SET_THEME_ID', id }) // fix any

type SetThemeActionType = ReturnType<typeof changeThemeId>

type StateType = {
    themeId:number
}
